import React from "react";
import { StyleSheet, Text, View } from "react-native";
import "react-native-gesture-handler";
import { createAppContainer } from "react-navigation";
import "react-navigation-tabs";
import { createMaterialTopTabNavigator } from "react-navigation-tabs";
import { MaterialCommunityIcons, FontAwesome5 } from "@expo/vector-icons";
import HomeScreen from "./src/HomeScreen";
import SearchScreen from "./src/SearchScreen";
import PostScreen from "./src/PostScreen";
import FeedScreen from "./src/FeedScreen";
import ProfileScreen from "./src/ProfileScreen";
/*import TopBar from "./src/TopBar";
home = home
profile = account-outline,
dots-vertical, dots-horizontal,
feed = heart/heart-outlne,
post = plus, Ionics-add = add, add-outline
send = send, send-outline,
FontAwesome5-search = search or FontAwesome5Brands-search = sistrix


*/
const TabNavigator = createMaterialTopTabNavigator(
    {
		Home: HomeScreen,
        Search: SearchScreen,
        Post: PostScreen,
        Feed: FeedScreen,
        Profile: ProfileScreen,
    },
    {
        defaultNavigationOptions: ({ navigation }) => ({
            tabBarIcon: ({ tintColor }) => {
                const { routeName } = navigation.state;
                let iconName = "home";
                if (routeName === "Profile") {
                    iconName = "user";
                }
                switch (routeName) {
                    case "Home":
                        iconName = "home";
                        break;
                    case "Search":
                        iconName = "search";
                        break;
                    case "Post":
                        iconName = "plus";
                        break;
                    case "feed":
                        iconName = "heart-outlined";
                        break;
                    case "Profile":
                        iconName = "account-outline";
                        break;
                }

                return (
                    <MaterialCommunityIcons
                        name={iconName}
                        size={24}
                        color={tintColor}
                    />
                );
                if (routeName === "search") {
                    return (
                        <FontAwesome5
                            name="search"
                            size={24}
                            color={tintColor}
                        />
                    );
                }
            },
        }),
        tabBarOptions: {
            style: {
                backgroundColor: "#1b1b1b",
            },
            activeTintColor: "white",
            inactiveTintColor: "darkgray",
            showIcon: true,
            showLabel: false,
            indicatorStyle: {
                opacity: 0,
            },
        },
        animationEnabled: true,
        swipeEnabled: false,
        tabBarPosition: "bottom",
    }
);

export default createAppContainer(TabNavigator);
